import { Component, OnInit } from '@angular/core';
import { MovieApiService } from '../movie-api.service';
import { MovieInterface } from '../movie.interface';
import { FormBuilder } from '@angular/forms';

@Component({
  selector: 'app-search-bar',
  templateUrl: './search-bar.component.html',
  styleUrls: ['./search-bar.component.css']
})
export class SearchBarComponent implements OnInit{
  searchValue = '';
  movies: any;
  searchForm = this.fb.nonNullable.group({
    searchValue: '',
  });

  constructor(
    private movieService: MovieApiService, 
    private fb: FormBuilder,
    ) {}

  ngOnInit(): void {
      this.fetchData();
  }

  fetchData(): void {
    this.movieService.getSearchMovie(this.searchValue).subscribe(movies => {
      this.movies = movies;
    })
  }

  onSearchSubmit(): void {
    this.searchValue = this.searchForm.value.searchValue ?? '';
    this.fetchData();
  }
}
<style>
  .search__wrapper {
    display: flex;
    flex: 1 1 0%;
    justify-content: flex-start;
    align-items: center;
  }
  .search__input {
    border-radius: 10px;
    height: 30px;
    width: 300px;
    padding-left: 15px;
    margin: 25px auto 25px auto;
    display: block;
  }
  .search__icon {
    margin-left: -10px;
    width: 20px;
    height: 20px;
    object-fit: contain;
    z-index: 10;
    position: absolute;
    right: 55%;
  }
</style>

<div class="container">
  <form [formGroup]="searchForm" class="searchBar" (ngSubmit)="onSearchSubmit()">
    <div class="search__wrapper">
      <input type="text" placeholder="Movie name" formControlName="searchValue" class="search__input" />
      <img src="./assets/search.svg" alt="search" class="search__icon" />
    </div>
  </form>
  <div *ngFor="let movie of movies">{{ movie.title }}</div>
</div>
